import { Article } from "@src/models/Article.model";
import { api } from "@src/services/api.service";
import { Pagination, Image, Spin, Skeleton } from "antd";
import React, { Suspense, useEffect, useState } from "react";
import { NavLink } from "react-router-dom";
import styled from "styled-components";
import "./articleList.css";
const List = styled.div`
  display: flex;
  flex-direction: column;
`;

export default function articleList() {
  const [index, setIndex] = useState(1);
  const [size, setSize] = useState(10);
  const [count, setCount] = useState(0);
  const [loading, setLoading] = useState(false);
  const [articleList, setArticleList] = useState<Article[]>([]);

  useEffect(() => {
    const skip = (index - 1) * size;
    const take = size;
    setLoading(true);
    api.getArticle(skip, take).then((result) => {
      setArticleList([...result]);
      setLoading(false);
      console.log("articleList :>> ", articleList);
    });
    api.getArticleCount().then((count) => {
      setCount(count);
    });
  }, [index, size]);

  const handlePaginationChangeEvent = (page: number, pageSize: number) => {
    setIndex(page);
    setSize(pageSize);
  };

  return (
    <List>
      <Spin spinning={loading}>
        {articleList.map((article, index) => {
          return article ? (
            <div key={article.article_name + index} className="article-cover">
              <div className="article-cover-left">
                <div className="article-cover-image">
                  {article.poster ? (
                    <Image
                      width={"100%"}
                      height={"100%"}
                      src={article.poster}
                      fallback="data:image/png;base64,"
                    />
                  ) : (
                    <Skeleton.Image />
                  )}
                </div>
              </div>
              <div className="article-cover-right">
                <NavLink to={"../article-text/" + article.id}>
                  {article.article_name}
                </NavLink>
                <div className="article-publish-time">
                  {new Date(article.create_time).toLocaleDateString()}{" "}
                  {new Date(article.create_time).toLocaleTimeString()}
                </div>
              </div>
            </div>
          ) : (
            <div>blank</div>
          );
        })}

        <Pagination
          onChange={handlePaginationChangeEvent}
          style={{
            margin: "10px auto",
            display: "flex",
            justifyContent: "center",
          }}
          size="small"
          total={count}
        />
      </Spin>
    </List>
  );
}
